import React from 'react'
import './Todo.css'
import Todo from './todo.jsx'

function List({ todos, setTodos }) {
    // ÌòïÏ†ú Ïª¥Ìè¨ÎÑåÌä∏Îì§Ïùò eventÎ°ú Ïù∏Ìï¥ Î∂ÄÎ™®Ïª¥Ìè¨ÎÑåÌä∏ÏóêÏÑú ÏàòÏ†ïÎêú propsÎ•º Î∞õÎäîÎã§ 

    // Îß§Í∞úÎ≥ÄÏàò IdÎäî ÎàÑÎ•∏todoÏóê id Í∞í
    const todoDeleteHandler = (Id) => {
        // filterÎäî Í∞íÏù¥ Ìä∏Î£®Ïù∏Í±∞Îßå Î∞òÌôò
        const Delete = todos.filter((todo) => {
            // ÏïÑÏù¥ÎîîÍ∞íÏù¥ Í∞ôÏßÄÏïäÏùÄÍ±∞Îßå DeleteÎ°ú Î∞òÌôò
            // Í∑∏Îüº ÎÇ¥Í∞Ä ÎàÑÎ•∏Î∂ÄÎ∂ÑÏùÄ ÏûêÎèôÏ†ÅÏúºÎ°ú ÏÇ≠Ï†ú 
            return todo.id !== Id
        })
        //ÏÇ≠Ï†úÎêúÎ∂ÄÎ∂ÑÎßå Ï†úÏô∏ÌïòÍ≥† Ïû¨Î†åÎçîÎßÅ
        setTodos(Delete)
    }
    console.log(todos.todo)

    // Îß§Í∞úÎ≥ÄÏàò IdÎäî ÎàÑÎ•∏todoÏóê id Í∞í
    const todoChangeHandler = (Id) => {
        const newTodos = todos.map((todo) => {
            if (todo.id === Id) {
                return { ...todo, isDone: !todo.isDone }
            } else {
                return { ...todo }
            }
        })
        // Î∞îÎÄêÎ∂ÄÎ∂Ñ Ïû¨Î†åÎçîÎßÅ
        setTodos(newTodos)
    }



    return (
        <div className='Todolist'>
            {/* ÌïòÎäîÏ§ë */}
            <h2 className="list-title">Working...üî•</h2>
            <div className="list-wrapper">
                {
                    todos.filter(todo => todo.isDone === false)
                        .map((todo) => {
                            return (
                                <Todo todo={todo} key={todo.id} setTodos={setTodos} todoDeleteHandler={todoDeleteHandler} todoChangeHandler={todoChangeHandler} />
                            )
                        })
                }

            </div>
            {/* ÎÅùÎÇúÏùº */}
            <h2 className="list-title">Done..! üéâ</h2>
            <div className="list-wrapper">

                {
                    todos.filter(todo => todo.isDone === true)
                        // .sort((a, b) => b - a)
                        .map((todo) => {
                            return (

                                <Todo todo={todo} key={todo.id} setTodos={setTodos} todoDeleteHandler={todoDeleteHandler} todoChangeHandler={todoChangeHandler} />
                            )
                        })
                }

            </div>



        </div>
    )
}

export default List
